import { createComponent } from "react-fela";

const headingLarge = ({ fontColor = "black" }) => ({
  font: "26px/30px Georgia, serif",
  padding: "0 0 16px 0",
  margin: 0,
  fontColor
});

export const HeadingLarge = createComponent(headingLarge, "h1");

const headingMedium = ({ fontColor = "black" }) => ({
  font: "18px/22px Georgia, serif",
  padding: "0 0 16px 0",
  margin: 0,
  fontColor
});

export const HeadingMedium = createComponent(headingMedium, "h1");

const paragraph = () => ({
  font: "14px/30px Georgia, serif",
  padding: "0 0 16px 0",
  margin: 0,
  fontColor: "black"
});

export const Paragraph = createComponent(paragraph, "p");
