import React from "react";
import { createRenderer } from "fela";
import { RendererProvider } from "react-fela";
import Head from "next/head";
import HomepageBlock from "../components/Homepage/homepageBlock.js";
import BlockContainer from "../components/Homepage/blockContainer";
import HeaderContent from "../components/Homepage/headerContent";
import Navigation from "../components/nav";

const animationData = [
  {
    animationName: "Image fade in on scroll",
    animationDescription: "Using intersection observer",
    animationKey: "fade-scroll-io",
    key: 1
  },
  {
    animationName: "Image fade in on scroll",
    animationDescription: "Using bounding client",
    animationKey: "fade-scroll-bc",
    key: 2
  },
  {
    animationName: "Image grow/shrink in on scroll",
    animationDescription: "Using bounding client",
    animationKey: "size-scroll-bc",
    key: 3
  },
  {
    animationName: "Image grow/shrink in on scroll",
    animationDescription: "Using intersection observer",
    animationKey: "size-scroll-io",
    key: 4
  },
  {
    animationName: "Parallax image",
    animationDescription: "Parallax imagery, shifting behaviour on scroll",
    animationKey: "parallax-image",
    key: '5'
  },
  {
    animationName: "Slide gallery",
    animationDescription: "Overlapping slide behaviour using sticky",
    animationKey: "slide-gallery",
    key: '6'
  },
  {
    animationName: "Sticky carousel",
    animationDescription: "Carousel behaviour with vertical numbered pips",
    animationKey: "sticky-vertical-carousel",
    key: '7'
  }
];

const renderer = createRenderer();

const Home = () => (
  <RendererProvider renderer={renderer}>
    <div>
      <Head>
        <title>Home | Animation Library</title>
      </Head>
      <Navigation animations={animationData} />
      <HeaderContent
        heading="Animations Library"
        subHeading="A library for animations built using React Hooks, Intersection Observer
        and more."
      />
      <BlockContainer>
        {animationData.map(item => (
          <HomepageBlock
            key={item.key}
            animationName={item.animationName}
            animationDescription={item.animationDescription}
            animationKey={item.animationKey}
          />
        ))}
      </BlockContainer>
    </div>
  </RendererProvider>
);

export default Home;
