import React, { useState, useRef } from "react";
import { createComponent } from "react-fela";
// import Image from "../../data/images/CH.jpg";
import getScreenHeight from "../../utils/getScreenHeight";
import getBoundingClient from "../../utils/getBounding";


const imageContainer = ({ width = "100%" }) => ({
  width,
  marginTop: "800px",
  marginBottom: "800px"
});

const ImageContainer = createComponent(imageContainer, "div");

const styledImage = () => ({
  width: "100%"
});

const StyledImage = createComponent(styledImage, "image", ["src"]);

const styledBlock = () => ({
  width: "100%",
  backgroundColor: "blue",
  height: "300px"
});

const StyledBlock = createComponent(styledBlock, "div");

const ResizeBC = () => {
  console.log(getScreenHeight().height, "<<<<< screen height");
  return (
    <ImageContainer>
      {/* <StyledImage src={Image} /> */}
      <StyledBlock />
    </ImageContainer>
  );
};

export default ResizeBC;
