import React, { useState } from "react";
import { createComponent } from "react-fela";
import Link from "next/link";
import { HeadingMedium } from "./StyledText";

const navigationBar = () => ({
  display: "flex"
});

const NavigationBar = createComponent(navigationBar, "nav");

const dropdownArea = () => ({
  float: "left",
  overflow: "hidden"
});

const DropdownArea = createComponent(dropdownArea, "div");

const dropdownButton = () => ({
  border: "none",
  outline: "none",
  ":hover": {
    cursor: "pointer"
  }
});

const DropdownButton = createComponent(dropdownButton, "button");

const dropdownList = () => ({
  display: "flex",
  flexDirection: "column",
  position: "absolute",
  backgroundColor: "white",
  minWidth: "250px",
  boxShadow: "0px 8px 16px 0px rgba(0,0,0,0.2)",
  zIndex: 20,
  padding: "6px"
});

const DropdownList = createComponent(dropdownList, "div");

const pageLink = () => ({
  textDecoration: "none",
  font: "14px/18px Times New Roman, serif",
  padding: "4px",
  ":hover": {
    cursor: "pointer",
    color: "#008f9c",

  }
});

const PageLink = createComponent(pageLink, "a");

const Navigation = ({ animations }) => (
  <NavigationBar>
    <HeadingMedium>Animation Library </HeadingMedium>
    <DropdownArea>
      <DropdownButton> V </DropdownButton>
      <DropdownList>
        {animations.map(item => (
          <Link href={`/${item.animationKey}`}>
            <PageLink>{item.animationName}</PageLink>
          </Link>
        ))}
      </DropdownList>
    </DropdownArea>
  </NavigationBar>
);

export default Navigation;
