import { createComponent } from "react-fela";

const headingLarge = () => ({
  font: "26px/30px Georgia, serif",
  padding: "0 0 16px 0",
  margin: 0
});

export const HeadingLarge = createComponent(headingLarge, "h1");

const headingMedium = () => ({
  font: "22px/30px Georgia, serif",
  padding: "0 0 16px 0",
  margin: 0,
  fontColor: 'black'
});

export const HeadingMedium = createComponent(headingMedium, "h1");

const paragraph = () => ({
  font: "18px/30px Georgia, serif",
  padding: "0 0 16px 0",
  margin: 0
});

export const Paragraph = createComponent(paragraph, "p");
