import { createComponent } from "react-fela";

const heading = () => ({
  font: "26px/30px Georgia, serif",
  padding: "0 0 16px 0",
  margin: 0
});

export const Heading = createComponent(heading, "h1");

const paragraph = () => ({
  font: "18px/30px Georgia, serif",
  padding: "0 0 16px 0",
  textAlign: "center",
  margin: 0
});

export const Paragraph = createComponent(paragraph, "p");
