const HomepageBlock = ({
  animationName,
  animationDescription,
  animationKey
}) => {
  return (
    <Container>
      <a href={`/${animationKey}`} className="homepage-block">
        <h3>{animationName}</h3>
        <p>{animationDescription}</p>
      </a>
    </Container>
  );
};

export default HomepageBlock;
