import React, { useState } from "react";
import { createComponent } from "react-fela";
import Link from "next/link";
import { HeadingMedium } from "./StyledText";

const navigationBar = () => ({
  display: "flex"
});

const NavigationBar = createComponent(navigationBar, "nav");

const dropdownArea = () => ({
  float: "left",
  overflow: "hidden",
  width: "300px"
});

const DropdownArea = createComponent(dropdownArea, "div");

const dropdownButton = () => ({
  border: "none",
  outline: "none",
  ":hover": {
    cursor: "pointer"
  }
});

const DropdownButton = createComponent(dropdownButton, "button");

const dropdownList = () => ({
  border: "none",
  outline: "none",
  ":hover": {
    cursor: "pointer"
  }
});

const DropdownList = createComponent(dropdownList, "div");

const Navigation = ({ animations }) => (
  <NavigationBar>
    <HeadingMedium>Animation Library </HeadingMedium>
    <DropdownArea>
      <DropdownButton> V </DropdownButton>
      <div>
        {animations.map(item => (
          <Link href={`/${item.animationKey}`}>{item.animationName}</Link>
        ))}
      </div>
    </DropdownArea>
  </NavigationBar>
);

export default Navigation;
