import React from "react";

const homepageBlocksData = [
  {
    animationName: "Image fade in on scroll",
    animationDescription: "Using intersection observer",
    animationKey: "fade-scroll-io"
  },
  {
    animationName: "Image fade in on scroll",
    animationDescription: "Using bounding client",
    animationKey: "fade-scroll-bc"
  }
];

const homepageBlock = ({
  animationName,
  animationDescription,
  animationKey
}) => {
  return (
    <a href="https://nextjs.org/learn" className="homepageBlock">
      <h3>{animationName}</h3>
      <p>{animationDescription}</p>
    </a>
  );
};

export default homepageBlock;
