import React from "react";
import { createComponent } from "react-fela";
import { HeadingMedium, Paragraph } from "../StyledText";

const container = () => ({
  padding: "16px",
  margin: "10px",
  border: "1px dotted black",
  width: "230px",
  textAlign: "center",
  textDecoration: "none"
});

const Container = createComponent(container, "p");

const link = () => ({
  textDecoration: "none"
});

const AnimationLink = createComponent(link, "a");


const HomepageBlock = ({
  animationName,
  animationDescription,
  animationKey
}) => {
  return (
    <Container>
      <AnimationLink href={`/${animationKey}`}>
        <HeadingMedium>{animationName}</HeadingMedium>
        <Paragraph>{animationDescription}</Paragraph>
      </AnimationLink>
    </Container>
  );
};

export default HomepageBlock;
