import React from "react";
import Head from "next/head";
import homepageBlock from "../components/homepageBlock.js";

const homepageBlocksData = [
  {
    animationName: "Image fade in on scroll",
    animationDescription: "Using intersection observer",
    animationKey: "fade-scroll-io",
    key: 1
  },
  {
    animationName: "Image fade in on scroll",
    animationDescription: "Using bounding client",
    animationKey: "fade-scroll-bc",
    key: 2
  },
  {
    animationName: "Parallax image",
    animationDescription: "Parallax imagery, shifting behaviour on scroll",
    animationKey: "fade-scroll-bc",
    key: 2
  }
];

const Home = () => (
  <div>
    <Head>
      <title>Home</title>
      <link rel="icon" href="/favicon.ico" />
    </Head>
    <div className="hero">
      <h1 className="title">Welcome to Next.js!</h1>
      <p className="description">
        To get started, edit <code>pages/index.js</code> and save to reload.
      </p>

      <div className="row">
        {homepageBlocksData.map(item => (
          <homepageBlock
            key={key}
            animationName={item.animationName}
            animationDescription={item.animationDescription}
            animationKey={item.animationKey}
          />
        ))}
      </div>
    </div>

    <style jsx>{`
      .hero {
        width: 100%;
        color: #333;
      }
      .title {
        margin: 0;
        width: 100%;
        padding-top: 80px;
        line-height: 1.15;
        font-size: 48px;
      }
      .title,
      .description {
        text-align: center;
      }
      .row {
        max-width: 880px;
        margin: 80px auto 40px;
        display: flex;
        flex-direction: row;
        justify-content: space-around;
      }
      .homepage-block {
        padding: 18px 18px 24px;
        width: 220px;
        text-align: left;
        text-decoration: none;
        color: #434343;
        border: 1px solid #9b9b9b;
      }
      .homepage-block:hover {
        border-color: #067df7;
      }
      .homepage-block h3 {
        margin: 0;
        color: #067df7;
        font-size: 18px;
      }
      .homepage-block p {
        margin: 0;
        padding: 12px 0 0;
        font-size: 13px;
        color: #333;
      }
    `}</style>
  </div>
);

export default Home;
