import React from "react";
import { createComponent } from "react-fela";

const heading = () => ({
  font: "24px/30px Georgia, serif",
  padding: ''
});

export const Heading = createComponent(heading, "div");

const paragraph = () => ({
    font: "16px/30px Georgia, serif"
  });

  export const Paragraph = createComponent(paragraph, "div");
