import React, { useEffect, useLayoutEffect, useRef, useState } from "react";
import { createComponent } from "react-fela";
import throttle from "lodash/fp/throttle";

// import Image from "../../data/images/CH.jpg";
import getScreenHeight from "../../utils/getScreenHeight";
import getBoundingClient from "../../utils/getBoundingClient";

const imageContainer = ({ width = "100%" }) => ({
  width,
  marginTop: "800px",
  marginBottom: "800px"
});

const ImageContainer = createComponent(imageContainer, "div", ["innerRef"]);

const styledImage = () => ({
  width: "100%"
});

const StyledImage = createComponent(styledImage, "image", ["src"]);

const styledBlock = () => ({
  width: "100%",
  backgroundColor: "blue",
  height: "300px"
});

const StyledBlock = createComponent(styledBlock, "div");

const ImageWrapper = ({ children }) => {
  const ref = useRef();
  const boundingClient = getBoundingClient(ref);
  const screenHeight = getScreenHeight();
  console.log(boundingClient, )
  return <ImageContainer innerRef={ref}>{children}</ImageContainer>;
};

const ResizeBC = () => {
  return (
    <ImageWrapper>
      <StyledBlock />
    </ImageWrapper>
  );
};

export default ResizeBC;
