import React from "react";
import { createComponent } from "react-fela";
import { HeadingMedium, Paragraph } from "../StyledText";
import Link from "next/link";

const container = () => ({
  padding: "16px",
  margin: "10px",
  border: "1px dotted black",
  width: "230px",
  textAlign: "center",
  textDecoration: "none",
  ":hover": {
    cursor: "pointer",

  }
});

const Container = createComponent(container, "div", ["onClick"]);

const HomepageBlock = ({
  animationName,
  animationDescription,
  animationKey
}) => {
  return (
    <Link href={`/${animationKey}`}>
      <Container>
        <HeadingMedium>{animationName}</HeadingMedium>
        <Paragraph>{animationDescription}</Paragraph>
      </Container>
    </Link>
  );
};

export default HomepageBlock;
