import React from "react";
import { createComponent } from "react-fela";
import { Heading, Paragraph } from "../StyledText/index";

const headingContainer = () => ({
  width: "100%",
  textAlign: "center"
});

const HeadingContainer = createComponent(headingContainer, "div");

const HeaderContent = ({head}) => {
  return (
    <HeadingContainer>
      <Heading>Animations Library</Heading>
      <Paragraph>
        A library for animations built using React Hooks, Intersection Observer
        and more.
      </Paragraph>
    </HeadingContainer>
  );
};
export default HeaderContent;
