import React, { useState } from "react";
import { createComponent } from "react-fela";
import Link from "next/link";
import { HeadingMedium } from "./StyledText";

const navigationBar = () => ({
  display: "flex",
  height: "48px",
  backgroundColor: "white"
});

const NavigationBar = createComponent(navigationBar, "nav");

const DropdownCaret = () => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    xmlnsXlink="http://www.w3.org/1999/xlink"
    version="1.1"
    style={"width:0; height:0; position:absolute; overflow:hidden;"}
  >
    <defs>
      <symbol
        viewBox="0 0 1030 638"
        aria-labelledby="bcsi-ant-caret-down-title"
        id="si-ant-caret-down"
      >
        <title id="bcsi-ant-caret-down-title">icon caret-down</title>
        <path d="M1017 68L541 626q-11 12-26 12t-26-12L13 68Q-3 49 6 24.5T39 0h952q24 0 33 24.5t-7 43.5z" />
      </symbol>
    </defs>
  </svg>
);

const dropdownArea = () => ({
  float: "left",
  overflow: "hidden"
});

const DropdownArea = createComponent(dropdownArea, "div");

const dropdownButton = () => ({
  border: "none",
  outline: "none",
  height: "18px",
  padding: "0 0 20px 16px",
  ":hover": {
    cursor: "pointer"
  }
});

const DropdownButton = createComponent(dropdownButton, "button");

const dropdownList = () => ({
  display: "flex",
  flexDirection: "column",
  position: "absolute",
  backgroundColor: "white",
  minWidth: "250px",
  boxShadow: "rgba(0, 0, 0, 0.1) 0px 10px 8px 0px",
  zIndex: 20,
  padding: "6px"
});

const DropdownList = createComponent(dropdownList, "div");

const pageLink = () => ({
  textDecoration: "none",
  font: "12px/18px Verdana, serif",
  padding: "4px",
  ":hover": {
    cursor: "pointer",
    color: "#008f9c",
    fontStyle: "bold"
  }
});

const PageLink = createComponent(pageLink, "a");

const Navigation = ({ animations }) => (
  <NavigationBar>
    <HeadingMedium>Animation Library </HeadingMedium>
    <DropdownArea>
      <DropdownButton>
        {" "}
        <DropdownCaret />{" "}
      </DropdownButton>
      <DropdownList>
        {animations.map(item => (
          <Link href={`/${item.animationKey}`}>
            <PageLink>{item.animationName}</PageLink>
          </Link>
        ))}
      </DropdownList>
    </DropdownArea>
  </NavigationBar>
);

export default Navigation;
