import React, { useLayoutEffect, useRef, useState } from "react";
import { createComponent } from "react-fela";
// import Image from "../../data/images/CH.jpg";
import getScreenHeight from "../../utils/getScreenHeight";
import getBoundingClient from "../../utils/getBoundingClient";

const imageContainer = ({ width = "100%" }) => ({
  width,
  marginTop: "800px",
  marginBottom: "800px"
});

const ImageContainer = createComponent(imageContainer, "div", ["innerRef"]);

const styledImage = () => ({
  width: "100%"
});

const StyledImage = createComponent(styledImage, "image", ["src"]);

const styledBlock = () => ({
  width: "100%",
  backgroundColor: "blue",
  height: "300px"
});

const StyledBlock = createComponent(styledBlock, "div");

const ResizeBC = () => {
  const ref = useRef(null);

  console.log(ref);

  return (
    <ImageContainer innerRef={ref}>
      <StyledBlock />
    </ImageContainer>
  );
};

export default ResizeBC;
