import React, { useRef } from "react";
import { createComponent } from "react-fela";
import Image from "../../data/images/CH.jpg";
import getScreenHeight from "../../utils/getScreenHeight";
import getBoundingClient from "../../utils/getBoundingClient";

const container = () => ({
  display: "flex",
  margin: "10px 100px",
  justifyContent: "center"
});

const Container = createComponent(container, "div");

const imageContainer = ({ opacity = "1" }) => ({
  opacity,
  width: "100%"
});

const ImageContainer = createComponent(imageContainer, "div", ["innerRef"]);

const styledImage = () => ({
  width: "100%"
});

const StyledImage = createComponent(styledImage, "img", ["src"]);

const getFadeOutOpacity = (boundingClient, screenHeight) => {
  const onScreenRatio =
    (boundingClient.top + boundingClient.height) / screenHeight;
  const percentageChange = onScreenRatio < 1 ? onScreenRatio : 1;
  return 1 - percentageChange;
};

const getFadeInOpacity = (boundingClient, screenHeight) => {
  const onScreenRatio = boundingClient.top / screenHeight;
  console.log(onScreenRatio * 3);
  const percentageChange = onScreenRatio < 1 ? onScreenRatio : 1;
  return 1 - percentageChange;
};

const ImageWrapper = ({ children, fadeIn }) => {
  const ref = useRef();
  const boundingClient = getBoundingClient(ref);
  const screenHeight = getScreenHeight().height;
  const opacity = fadeIn
    ? getFadeInOpacity(boundingClient, screenHeight)
    : getFadeOutOpacity(boundingClient, screenHeight);
  return (
    <ImageContainer opacity={opacity} innerRef={ref}>
      {children}
    </ImageContainer>
  );
};

const FadeBC = ({ fadeIn }) => {
  return (
    <Container>
      <ImageWrapper fadeIn={fadeIn}>
        <StyledImage src={Image} />
      </ImageWrapper>
    </Container>
  );
};

export default FadeBC;
