import React from "react";
import { createComponent } from "react-fela";
import getScreenHeight from "../../utils/getScreenHeight";
import getBoundingClient from "../../utils/getBoundingClient";

const slideContainer = () => ({
  position: "relative"
});

const SlideContainer = createComponent(slideContainer, "div");

const slideWrapper = ({ backgroundColor = "blue", zIndex }) => ({
  width: "100%",
  position: "sticky",
  top: 0,
  height: "100vh",
  backgroundColor,
  overflow: "hidden",
  zIndex
});

const SlideWrapper = createComponent(slideWrapper, "div");

const slideImage = () => ({
  width: "100%",
  objectFit: "contain"
});
const SlideImage = createComponent(slideImage, "img", ["src"]);

const Slide = ({ zIndex, imageSrc }) => {
  return (
    <SlideWrapper zIndex={zIndex}>
      <SlideImage src={imageSrc} />
    </SlideWrapper>
  );
};

const StickySlides = ({ images }) => (
  <SlideContainer>
    {images.map((item, index) => (
      <Slide zIndex={(index + 1) * 2} imageSrc={item} />
    ))}
  </SlideContainer>
);

export default StickySlides;
